//==========================
channel.on("drawline", function (payload) {
  ctx.moveTo(payload.from.x, payload.from.y);
  ctx.lineTo(payload.to.x, payload.to.y);
  ctx.stroke();
});

channel.on("clear", function (payload) {
  console.log("Clearing canvas.");
  clearCanvas();
});

